mapper.select:
select CONCAT(left(userName,1),'**') xhUserName,CONCAT(left(phone,3),'******',right(phone,2)) xhPhone,UNIX_TIMESTAMP(now())- UNIX_TIMESTAMP(createTime) secondAgo from product_type_user where 1=1

	deal=#{deal}

order by createTime desc

 		select count(*) from product_type_user where 1=1

			and userName like'%${userName}%'


			and phone like'%${phone}%'


			and createTime between #{createTimeStart} and #{createTimeEnd}


 			and deal=#{deal}

 		select * from product_type_user where 1=1

			and userName like'%${userName}%'


			and phone like'%${phone}%'


			and createTime between #{createTimeStart} and #{createTimeEnd}


 			and deal=#{deal}
